void ApplyColorMap(const cv::Mat& source, const cv::Mat& colormap, cv::Mat& destination)
{
	cv::Mat ScaledImage;
	cv::normalize(source, ScaledImage, 0, 255, cv::NORM_MINMAX);
	ScaledImage.convertTo(ScaledImage, CV_8UC1);

	cv::Mat Gray3Channels;
	cv::cvtColor(source, Gray3Channels, CV_GRAY2BGR);

	cv::Mat output;
	cv::LUT(Gray3Channels, colormap, destination);
}

void SaveImage(const char* filename, const cv::Mat& source, unsigned char bgrmap[]) 
{
	cv::Mat colorimg;
	cv::Mat colormap = cv::Mat(1, 256, CV_8UC3, bgrmap);
	ApplyColorMap(source, colormap, colorimg);
	imwrite(filename, colorimg);
}

